const scrollable=document.querySelector(".scrollable");let current=0;let target=0;let ease=0.075;const siLerp=(start,end,time)=>{return start*(1-time)+end*time;};const setBodyHeight=()=>{document.body.style.height=`${scrollable.getBoundingClientRect().height}px`;};const onWindowResize=()=>{window.addEventListener("resize",setBodyHeight);};const smoothScroll=()=>{target=window.scrollY;current=siLerp(current,target,ease);scrollable.style.transform=`translate3d(0, ${-current}px, 0)`;requestAnimationFrame(smoothScroll);};setBodyHeight();smoothScroll();onWindowResize();